(16, 7, NULL, 'cool-beach-summer-dress', 'CBSUMMER-DRS-015', 12, 65.00, NULL, '/storage/media/3Hkc9txSHbNi11ExWRKUr9mtXAX4pYNrJso9IheY.jpg', '[\"\\/storage\\/media\\/np1qehrQj1ve5xbdrC0UlVkZ05E95tbvsbbS2qaR.jpg\",\"\\/storage\\/media\\/Eyah1vRJ8RIoZiretBbKaASF68BlxXw7fIaBi98Z.jpg\",\"\\/storage\\/media\\/5sQAnLM4fvSgVRGMZac4CWd3X66FZT3Txerx0gaT.jpg\"]', 1, 1, 1, 'Cool Beach Summer Dress – Lightweight & Breezy Beachwear for Women', 'Stay cool in the Cool Beach Summer Dress – a breathable, relaxed-fit dress perfect for beach days, vacations, or casual summer wear. Lightweight, stylish, and easy to wear.', '2025-07-08 03:45:48', '2025-07-08 07:48:18'),
(17, 5, 5, 'urban-sands-linen-set', 'USLINEN-SET-016', 30, 100.00, 78.00, '/storage/media/sgrAMhrQxtOaGp8ft8N1BUXk79ksY2IGKLJjxb10.jpg', '[\"\\/storage\\/media\\/mjYKBo8oMRYPRPCtdRb4oj9QDNv5xHIvJZqsZcUT.jpg\"]', 1, 0, 1, 'Urban Sands Linen Set – Lightweight Two-Piece Outfit for Summer Comfort', 'Discover the Urban Sands Linen Set – a breathable two-piece outfit made from natural linen. Perfect for travel, city wear, or casual summer days with elevated style.', '2025-07-08 03:52:03', '2025-07-09 05:16:32'),
(18, 6, NULL, 'sunset-shore-bikini-set', 'SSBIKINI-SET-017', 50, 125.00, 89.00, '/storage/media/NocgKADQGDNlVErAaX5rzSShqnHBXG4myXzYUmeW.jpg', '[\"\\/storage\\/media\\/6nGOqyhE4yODshM4heXjc71taZAl0QOmd8o0ukR9.jpg\",\"\\/storage\\/media\\/LYxN4qfj90hFMJLVH9BS5szTsp0y3Z2nuTnLOKMB.jpg\",\"\\/storage\\/media\\/9gdb2zzeXL5ILobBznofDFpp6MFQfL7Y31oUIQkD.jpg\"]', 1, 0, 1, 'Sunset Shore Bikini Set – Stylish Two-Piece Swimwear for Beach & Pool', 'Make a splash in the Sunset Shore Bikini Set – a vibrant two-piece swimwear set designed for comfort, support, and tropical vibes. Perfect for summer, pool days, and beach vacations.', '2025-07-08 03:56:43', '2025-07-08 07:47:50'),
(19, 7, NULL, 'crimson-desert-tulle-dress', 'CDTULLE-DRS-018', 25, 99.00, 88.00, '/storage/media/prC6yVuiunc4GArZwfZijRmSxrrMMCAgHJGac2je.jpg', '[\"\\/storage\\/media\\/ATJFfdRKyEvrV6qGLFn2SzPGoHu9UyFaptOFYE35.jpg\"]', 1, 0, 1, 'Crimson Desert Tulle Dress – Elegant Layered Evening Gown for Women', 'Shop the Crimson Desert Tulle Dress – a bold crimson evening gown with layered tulle, perfect for weddings, galas, and formal occasions. Dramatic, elegant, and unforgettable.', '2025-07-08 04:05:42', '2025-07-08 07:47:38'),
(20, 6, NULL, 'golden-coast-bikini-set', 'GCBIKINI-SET-019', 22, 39.00, 38.50, '/storage/media/vodOU6MFdgNSKGzYRcVjQBiESPNvBXZuM5O1sQGH.jpg', '[\"\\/storage\\/media\\/NS3FMy8RaxNhqSFffpAj6SZgClcEVOKH0KM3oPzq.jpg\",\"\\/storage\\/media\\/cTRvEhATN3IoGgQbIy1FHJHYCAm8EpcNbq6KITxR.jpg\",\"\\/storage\\/media\\/cuf6nKu0Rk4hhzPnKto5axRYwS3GjMJmNfVw4f4C.jpg\"]', 1, 0, 1, 'Golden Coast Bikini Set – Shimmering Two-Piece Swimwear for Women', 'Turn heads in the Golden Coast Bikini Set – a stylish two-piece swimsuit with a soft gold finish, perfect for pool parties, beach days, and sun-drenched getaways.', '2025-07-08 04:22:04', '2025-07-08 23:34:27');
