INSERT INTO `product_contents` (`id`, `language_code`, `product_id`, `title`, `description`, `short_description`, `created_at`, `updated_at`) VALUES
(1, 'en', 1, 'Future AI Robot Toys', '<p><strong>Future AI Robot Toys</strong> bring cutting-edge technology to playtime! These advanced robots offer:<br>✅ <strong>Voice &amp; Gesture Control</strong> – Respond to commands and movements.<br>✅ <strong>Coding &amp; Programming</strong> – Teach kids coding basics through fun, interactive tasks.<br>✅ <strong>Emotion Recognition</strong> – Detect and react to emotions for engaging interactions.<br>✅ <strong>STEM Education</strong> – Foster skills in robotics, logic, and problem-solving.<br>✅ <strong>Durable &amp; Rechargeable</strong> – Built to last with kid-friendly materials.</p><p>Ideal for home learning, classrooms, or as a gift, these robots prepare young minds for a tech-driven future!</p>', '<p><strong>Future AI Robot Toys</strong> – Interactive, programmable AI robots with voice control, emotion detection, and STEM learning features. Perfect for kids ages 6+ to explore coding and robotics!</p>', '2025-04-19 05:51:31', '2025-04-19 05:51:31'),
(2, 'ar', 1, 'ألعاب روبوتات ذكاء اصطناعي مستقبلية', '<p><strong>ألعاب روبوتات ذكاء اصطناعي مستقبلية</strong> تجلب أحدث التقنيات إلى وقت اللعب! تتميز هذه الروبوتات المتطورة بما يلي:<br>✅ <strong>التحكم بالصوت والإيماءات</strong> – تستجيب للأوامر والحركات.<br>✅ <strong>البرمجة والتشفير</strong> – تُعلّم الأطفال أساسيات البرمجة عبر أنشطة تفاعلية ممتعة.<br>✅ <strong>التعرف على المشاعر</strong> – تكتشف المشاعر وتتفاعل معها.<br>✅ <strong>تعليم STEM</strong> – تنمي مهارات الروبوتات والمنطق وحل المشكلات.<br>✅ <strong>متينة وقابلة لإعادة الشحن</strong> – مصنوعة من مواد آمنة للأطفال.</p><p>مثالية للتعلم المنزلي، الفصول الدراسية، أو كهدية، تُعد هذه الروبوتات الأطفال لعالم تقني متطور!</p>', '<p><strong>ألعاب روبوتات ذكاء اصطناعي مستقبلية</strong> – روبوتات تفاعلية قابلة للبرمجة مع تحكم صوتي، اكتشاف المشاعر، وميزات تعليمية في مجال STEM. مثالية للأطفال من سن 6+ لتعلم البرمجة والروبوتات!<br>&nbsp;</p>', '2025-04-19 05:51:31', '2025-04-19 05:51:31'),
(3, 'en', 2, 'Awesome Men T-Shirt', '<p><strong>Elevate Your Casual Style</strong><br>Our Awesome Men T-Shirt is crafted from 100% premium cotton for ultimate softness and breathability. Features include:<br>✅ <strong>Premium Fabric</strong> - Soft, durable 100% cotton<br>✅ <strong>Comfort Fit</strong> - Relaxed fit for all-day comfort<br>✅ <strong>Modern Design</strong> - Stylish minimalist look<br>✅ <strong>Versatile Wear</strong> - Perfect for casual, work, or workouts<br>✅ <strong>Multiple Colors</strong> - Classic and trendy color options</p><p>Machine washable. Available in sizes S-XXL.</p>', '<p>Premium quality men\'s t-shirt with a comfortable fit and stylish design. Perfect for casual wear, outings, and everyday use. Available in multiple colors and sizes.</p>', '2025-04-19 05:56:24', '2025-04-19 05:56:24'),
(4, 'ar', 2, 'تي شيرت رجالي رائع', '<p><strong>ارتقِ بأسلوبك اليومي</strong><br>تي شيرتنا الرجالي الرائع مصنوع من قطن 100% عالي الجودة للنعومة والتهوية المثالية. المميزات:<br>✅ <strong>نسيج ممتاز</strong> - قطن 100% ناعم ومتين<br>✅ <strong>قصّة مريحة</strong> - تصميم واسع للراحة طوال اليوم<br>✅ <strong>تصميم عصري</strong> - مظهر بسيط أنيق<br>✅ <strong>متعدد الاستخدامات</strong> - مثالي للعمل، الرياضة أو الحياة اليومية<br>✅ <strong>ألوان متعددة</strong> - خيارات كلاسيكية وعصرية</p><p>يمكن غسله بالماكينة. متوفر بمقاسات من S إلى XXL.</p>', '<p>تي شيرت رجالي عالي الجودة بتصميم مريح وأنيق. مثالي للارتداء اليومي والخروج. متوفر بعدة ألوان ومقاسات.<br>&nbsp;</p>', '2025-04-19 05:56:24', '2025-04-19 05:56:24'),
(5, 'en', 3, 'Showpic Photography', '<p><strong>Bring Nature\'s Beauty to Your Space</strong><br>Our Nature Photography collection features:<br>✅ <strong>High-Quality Prints</strong> - Premium archival paper with vibrant colors<br>✅ <strong>Diverse Subjects</strong> - Landscapes, wildlife, forests, and waterfalls<br>✅ <strong>Multiple Sizes</strong> - From small frames to large wall art<br>✅ <strong>Ready to Hang</strong> - Comes framed or unframed options<br>✅ <strong>Unique Perspectives</strong> - Captured by professional photographers</p><p>Each piece celebrates the wonder of the natural world. Makes a perfect gift for nature lovers!</p>', '<p>Stunning nature photography prints capturing breathtaking landscapes, wildlife, and natural wonders. Perfect for home or office decoration.<br>&nbsp;</p>', '2025-04-19 06:06:48', '2025-07-08 00:56:14'),
(6, 'ar', 3, 'تصوير الطبيعة', '<p><strong>اجلب جمال الطبيعة إلى مساحتك</strong><br>تضم مجموعة تصوير الطبيعة لدينا:<br>✅ <strong>طباعة عالية الجودة</strong> - ورق أرشيفي ممتاز بألوان زاهية<br>✅ <strong>مواضيع متنوعة</strong> - مناظر طبيعية، حياة برية، غابات وشلالات<br>✅ <strong>مقاسات متعددة</strong> - من الإطارات الصغيرة إلى لوحات الحائط الكبيرة<br>✅ <strong>جاهز للتعليق</strong> - خيارات مع إطار أو بدونه<br>✅ <strong>زوايا تصوير فريدة</strong> - التقطها مصورون محترفون</p><p>كل قطعة تحتفل بعجائب العالم الطبيعي. هدية مثالية لعشاق الطبيعة!</p>', '<p>لوحات تصوير طبيعة مذهلة تلتقط المناظر الطبيعية الخلابة والحياة البرية. مثالية لتزيين المنزل أو المكتب.</p>', '2025-04-19 06:06:48', '2025-04-19 06:06:48'),
(7, 'en', 4, 'Hemp seed shampoo', '<p><strong>Revitalize Your Hair Naturally</strong><br>Our Hemp Seed Shampoo is specially formulated to:<br>✅ <strong>Strengthen Hair</strong> - Rich in omega fatty acids to reduce breakage<br>✅ <strong>Moisturize Scalp</strong> - Hemp seed oil nourishes dry, itchy scalp<br>✅ <strong>Promote Growth</strong> - Stimulates hair follicles for thicker hair<br>✅ <strong>Gentle Cleansing</strong> - Sulfate-free formula safe for daily use<br>✅ <strong>Natural Ingredients</strong> - With argan oil, coconut oil, and vitamin E</p><p>Free from parabens, silicones, and artificial colors. Vegan and cruelty-free.</p>', '<p>Nourishing hemp seed shampoo for stronger, healthier hair. Infused with natural oils to cleanse, moisturize, and promote hair growth. Suitable for all hair types.</p>', '2025-04-19 06:13:27', '2025-04-19 06:13:27'),
(8, 'ar', 4, 'شامبو بذور القنب', '<p><strong>جدد شعرك طبيعياً</strong><br>تم تصنيع شامبو بذور القنب لدينا خصيصاً ل:<br>✅ <strong>تقوية الشعر</strong> - غني بأحماض أوميغا الدهنية لتقليل التقصف<br>✅ <strong>ترطيب فروة الرأس</strong> - زيت بذور القنب يغذي الفروة الجافة<br>✅ <strong>تعزيز النمو</strong> - يحفز بصيلات الشعر للحصول على شعر أكثر كثافة<br>✅ <strong>تنظيف لطيف</strong> - خالي من الكبريتات وآمن للاستخدام اليومي<br>✅ <strong>مكونات طبيعية</strong> - يحتوي على زيت الأرجان، زيت جوز الهند وفيتامين E</p><p>خالي من البارابين والسيليكون والألوان الصناعية. نباتي ولم يتم اختباره على الحيوانات.</p>', '<p>شامبو مغذي ببذور القنب لشعر أقوى وأكثر صحة. غني بالزيوت الطبيعية لتنظيف، ترطيب وتعزيز نمو الشعر. مناسب لجميع أنواع الشعر.</p>', '2025-04-19 06:13:27', '2025-04-19 06:13:27'),
(9, 'en', 5, 'iPhone prototype', '<p><strong>Own a Piece of Apple History</strong><br>This genuine iPhone prototype offers:<br>✅ <strong>Historical Significance</strong> - Early-stage development model<br>✅ <strong>Unique Design Features</strong> - Different from final production models<br>✅ <strong>Collector\'s Item</strong> - Extremely rare opportunity<br>✅ <strong>Display-Ready</strong> - Perfect for tech museums or private collections<br>✅ <strong>Documentation Included</strong> - With authenticity verification</p><p>Note: This prototype is non-functional and intended for educational/display purposes only. May show signs of wear from development testing.</p>', '<p>Rare collector\'s item: An authentic iPhone prototype unit showcasing Apple\'s innovative design process. Not for consumer use - for display and collection purposes only.</p>', '2025-04-19 06:23:16', '2025-04-19 06:23:16'),
(10, 'ar', 5, 'نموذج آي فون الأولي', '<p><strong>امتلك قطعة من تاريخ آبل</strong><br>يقدم هذا النموذج الأولي الأصلي لآي فون:<br>✅ <strong>أهمية تاريخية</strong> - نموذج تطوير من المراحل المبكرة<br>✅ <strong>ميزات تصميم فريدة</strong> - يختلف عن نماذج الإنتاج النهائية<br>✅ <strong>قطعة لهواة الجمع</strong> - فرصة نادرة جداً<br>✅ <strong>جاهز للعرض</strong> - مثالي للمتاحف التقنية أو المجموعات الخاصة<br>✅ <strong>يشمل وثائق</strong> - مع إثبات الأصالة</p><p>ملاحظة: هذا النموذج غير عامل ومخصص لأغراض التعليم/العرض فقط. قد تظهر عليه علامات استخدام من اختبارات التطوير.</p>', '<p>قطعة نادرة لهواة الجمع: نموذج أولي أصلي لآي فون يعرض عملية التصميم المبتكرة من آبل. غير مخصص للاستخدام - للعرض والجمع فقط.<br>&nbsp;</p>', '2025-04-19 06:23:16', '2025-04-19 06:23:16'),
(11, 'en', 6, 'Red diamond perfume', '<p><strong>The Jewel of Fragrances</strong><br>Our Red Diamond Perfume captures luxury in a bottle:<br>🔴 <strong>Top Notes</strong>: Blood orange, saffron, pomegranate<br>🔴 <strong>Heart Notes</strong>: Red rose, jasmine sambac, orchid<br>🔴 <strong>Base Notes</strong>: Amber, vanilla, oud, musk<br>✨ <strong>Key Features</strong>:</p><p>18-24 hour lasting power</p><p>Concentrated eau de parfum</p><p>Handcrafted crystal-inspired bottle</p><p>Vegan &amp; cruelty-free</p><p>A statement fragrance for those who demand extraordinary elegance.</p>', '<p>Luxurious fragrance with rare red diamond essence - a bold, sensual blend of exotic florals and warm spices. Long-lasting elegance in every drop.</p>', '2025-04-19 06:29:13', '2025-04-19 06:29:13'),
(12, 'ar', 6, 'عطر الألماس الأحمر', '<p><strong>جوهرة العطور</strong><br>عطر الألماس الأحمر يجسد الفخامة في زجاجة:<br>🔴 <strong>الرائحة الأولى</strong>: برتقال الدم، زعفران، رمان<br>🔴 <strong>قلب العطر</strong>: ورد أحمر، ياسمين، أوركيد<br>🔴 <strong>قاعدة العطر</strong>: عنبر، فانيليا، عود، مسك<br>✨ <strong>المميزات الرئيسية</strong>:</p><p>يدوم من 18 إلى 24 ساعة</p><p>عطر مركز من نوع أو دي بارفيوم</p><p>زجاجة مصممة على شكل بلورة كريستال</p><p>نباتي ولم يتم اختباره على الحيوانات</p><p>عطر يجسد الأناقة الاستثنائية لأصحاب الذوق الرفيع.</p>', '<p>عطر فاخر مستوحى من جوهرة الألماس الأحمر - مزيج جريء وأنيق من الزهور النادرة والتوابل الدافئة. أناقة تدوم طويلاً بكل قطرة.<br>&nbsp;</p>', '2025-04-19 06:29:13', '2025-04-19 06:29:13'),
(13, 'en', 7, '3d suduku toy', '<p><strong>Experience Sudoku Like Never Before</strong><br>Our 3D Sudoku Toy transforms traditional number puzzles into a hands-on experience:</p><p>🧩 <strong>3D Game Board</strong> - 9x9x9 cube structure for multi-dimensional play<br>🌈 <strong>Color-Coded Pieces</strong> - 81 physical number tiles in 9 vibrant colors<br>🧠 <strong>4 Difficulty Levels</strong> - From beginner to expert challenges<br>👐 <strong>Tactile Learning</strong> - Improves spatial reasoning &amp; problem-solving skills<br>🎁 <strong>Complete Set Includes</strong>:</p><p>Foldable 3D game stand</p><p>81 number pieces (1-9)</p><p>Solution booklet</p><p>Storage pouch</p><p>Perfect for ages 12+ - makes math fun for students and adults alike!</p>', '<p>3D Sudoku Toy - Multi-Dimensional Brain Teaser Puzzle Game for Adults &amp; Kids</p>', '2025-04-19 06:38:34', '2025-04-19 06:38:34'),
(14, 'ar', 7, 'لعبة سودوكو ثلاثية الأبعاد', '<p><strong>جرب سودوكو بطريقة جديدة تماماً</strong><br>تحول لعبة السودوكو ثلاثية الأبعاد الألغاز الرقمية إلى تجربة تفاعلية:</p><p>🧩 <strong>لوحة لعب ثلاثية</strong> - هيكل مكعب 9x9x9 للعب متعدد الأبعاد<br>🌈 <strong>قطع ملونة</strong> - 81 قطعة رقمية بتسعة ألوان زاهية<br>🧠 <strong>4 مستويات صعوبة</strong> - من المبتدئين إلى المحترفين<br>👐 <strong>تعلم حسي</strong> - يحسن المنطق المكاني ومهارات حل المشكلات<br>🎁 <strong>المجموعة الكاملة تشمل</strong>:</p><p>حامل لعبة قابل للطي</p><p>81 قطعة رقمية (1-9)</p><p>كتيب الحلول</p><p>حافظة تخزين</p><p>مثالية لمن عمرهم 12+ - تجعل الرياضيات ممتعة للطلاب والكبار!</p>', '<p>Challenge your mind with our 3D Sudoku Toy - the physical, color-coded puzzle game that boosts logic skills. Includes 81 pieces &amp; 4 difficulty levels.</p>', '2025-04-19 06:38:34', '2025-04-19 06:38:34'),
(15, 'en', 8, 'Fossil watch', '<p><strong>Timeless Style Meets Modern Reliability</strong></p><p>This authentic Fossil watch combines vintage inspiration with contemporary functionality:</p><p>✔ <strong>Premium Materials</strong>:</p><p>Genuine leather strap with contrast stitching</p><p>Stainless steel case (42mm)</p><p>Mineral crystal glass face</p><p>✔ <strong>Precision Timekeeping</strong>:</p><p>Japanese quartz movement</p><p>3-hand analog display with date window</p><p>50m water resistance</p><p>✔ <strong>Design Details</strong>:</p><p>Embossed Fossil logo</p><p>Deployment clasp closure</p><p>Interchangeable strap system</p><p>Perfect for both casual and formal occasions. Includes 2-year international warranty.</p>', '<p>Classic Fossil watch featuring premium craftsmanship with genuine leather strap and stainless steel case. Water-resistant and powered by precise quartz movement.</p>', '2025-04-19 06:49:11', '2025-04-19 06:49:11'),
(16, 'ar', 8, 'ساعة فوسيل', '<p><strong>أناقة خالدة وموثوقية عصرية</strong></p><p>ساعة فوسيل الأصيلة تجمع بين الإلهام الكلاسيكي والأداء الحديث:</p><p>✔ <strong>مواد عالية الجودة</strong>:</p><p>حزام جلد طبيعي مع خياطة متباينة</p><p>علبة من الفولاذ المقاوم للصدأ (42 ملم)</p><p>زجاج معدني مقاوم للخدش</p><p>✔ <strong>دقة في الوقت</strong>:</p><p>حركة كوارتز يابانية</p><p>عرض تناظري مع نافذة التاريخ</p><p>مقاومة للماء حتى 50 متر</p><p>✔ <strong>تفاصيل التصميم</strong>:</p><p>شعار فوسيل بارز</p><p>إغلاق بمشبك أمان</p><p>نظام أحزمة قابلة للتبديل</p><p>مثالية للمناسبات الرسمية واليومية. تشمل ضمان دولي لمدة سنتين.</p>', '<p>ساعة فوسيل الكلاسيكية بتصميم أنيق وحزام جلد طبيعي وعلبة من الفولاذ المقاوم للصدأ. مقاومة للماء وتعمل بحركة كوارتز دقيقة.</p>', '2025-04-19 06:49:11', '2025-04-19 06:49:11'),
(17, 'en', 9, 'Water bottle', '<p><strong>The Ultimate Hydration Companion</strong></p><p>✔ <strong>Temperature Control</strong>:</p><p>Keeps cold for 24 hours</p><p>Maintains heat for 12 hours</p><p>Double-wall vacuum insulation</p><p>✔ <strong>Premium Features</strong>:</p><p>100% BPA-free stainless steel</p><p>Leak-proof screw-on lid</p><p>Sweat-proof exterior</p><p>Wide mouth for easy filling</p><p>✔ <strong>User-Friendly Design</strong>:</p><p>Carry handle + built-in straw</p><p>Fits most cup holders</p><p>Easy-grip textured surface</p><p>Dishwasher safe</p><p>Available in 500ml/750ml/1L sizes. Perfect for gym, office or outdoor adventures!</p>', '<p>Stay hydrated in style with our premium double-walled insulated water bottle. Keeps drinks cold for 24 hours or hot for 12 hours. BPA-free &amp; leak-proof design.</p>', '2025-04-19 07:03:20', '2025-04-19 07:03:20'),
(18, 'ar', 9, 'زجاجة ماء عازلة مميزة', '<p><strong>رفيقك المثالي للترطيب</strong></p><p>✔ <strong>تحكم في درجة الحرارة</strong>:</p><p>يحافظ على البرودة لمدة 24 ساعة</p><p>يحتفظ بالحرارة لمدة 12 ساعة</p><p>عزل مفرغ مزدوج الجدار</p><p>✔ <strong>مميزات ممتازة</strong>:</p><p>ستانلس ستيل خالي 100% من BPA</p><p>غطاء ملولب مقاوم للتسرب</p><p>سطح خارجي مقاوم للتعرق</p><p>فتحة واسعة لسهولة الملء</p><p>✔ <strong>تصميم سهل الاستخدام</strong>:</p><p>مقبض حمل + شمعة مدمجة</p><p>تناسب معظم حوامل الأكواب</p><p>سطح مضلع لسهولة الإمساك</p><p>آمن لغسالة الصحون</p><p>متوفرة بمقاسات 500مل/750مل/1لتر. مثالية للنادي الرياضي، المكتب أو الرحلات!</p>', '<p>حافظ على رطوبتك بأناقة مع زجاجة الماء العازلة المميزة. تحتفظ بالمشروبات باردة لمدة 24 ساعة أو ساخنة لمدة 12 ساعة. تصميم مقاوم للتسرب وخالي من مادة BPA.</p>', '2025-04-19 07:03:20', '2025-04-19 07:03:20'),
(19, 'en', 10, 'Nature photography', '<p><strong>Immerse Yourself in the Wonders of Nature</strong></p><p>Our <strong>Nature Photography</strong> collection is a celebration of Earth’s untouched beauty, frozen in time through the lens of passionate photographers. From majestic mountain ranges and tranquil forests to intimate close-ups of wildlife and delicate flora, each photograph tells a story of the planet’s awe-inspiring diversity.</p><p><strong>Why Choose Our Nature Photography?</strong><br>✔ <strong>High-Quality Prints</strong> – Crisp, vibrant, and professionally edited for maximum impact.<br>✔ <strong>Diverse Subjects</strong> – Landscapes, wildlife, macro shots, and seasonal wonders.<br>✔ <strong>Perfect for Any Space</strong> – Ideal for home decor, offices, or as a heartfelt gift.<br>✔ <strong>Eco-Conscious</strong> – Printed on sustainable materials to honor the nature we cherish.</p><p>Whether you\'re an art lover, a nature enthusiast, or simply seeking inspiration, these photographs bring the outdoors inside—transforming any room into a serene escape.</p><p><strong>Available in multiple sizes and framing options to suit your style.</strong></p>', '<p><i>Capture the raw beauty of the natural world with stunning <strong>Nature Photography</strong>. Each image showcases breathtaking landscapes, wildlife, and intricate details—bringing the serenity and wonder of nature into your space.</i></p>', '2025-04-24 12:50:10', '2025-04-24 12:50:10'),
(20, 'ar', 10, 'تصوير الطبيعة', '<p><strong>اغمر نفسك في عجائب الطبيعة</strong><br>مجموعة <strong>الصور الفوتوغرافية للطبيعة</strong> الخاصة بنا هي احتفال بالجمال البريء للأرض، مُجمد في الزمن عبر عدسة مصورين شغوفين. من سلاسل الجبال الشامخة والغابات الهادئة إلى اللقطات الحميمية للحياة البرية والنباتات الدقيقة، كل صورة تحكي قصة عن التنوع المدهش لكوكبنا.</p><p><strong>لماذا تختار صورنا الطبيعية؟</strong><br>✔ <strong>طباعة عالية الجودة</strong> – صور واضحة، نابضة بالحياة، ومعدة احترافياً لأقصى تأثير.<br>✔ <strong>مواضيع متنوعة</strong> – مناظر طبيعية، حياة برية، لقطات مقرّبة، وعجائب موسمية.<br>✔ <strong>مثالية لأي مساحة</strong> – مناسبة لتزيين المنزل، المكاتب، أو كهدية ذات معنى.<br>✔ <strong>صديقة للبيئة</strong> – مطبوعة على مواد مستدامة لتكريم الطبيعة التي نعتز بها.</p><p>سواء كنت من عشاق الفن، محباً للطبيعة، أو ببساطة تبحث عن الإلهام، هذه الصور تجلب الخارج إلى الداخل—محولة أي غرفة إلى ملاذ ساحر.</p><p><strong>متوفرة بمقاسات متعددة وخيارات إطارات تناسب ذوقك.</strong></p>', '<p><strong>التقط الجمال الخام للطبيعة بصور فوتوغرافية مذهلة. كل صورة تعرض مناظر طبيعية خلابة، حياة برية، وتفاصيل دقيقة—تجلب سحر الطبيعة وهدوئها إلى عالمك.</strong></p>', '2025-04-24 12:50:10', '2025-04-24 12:50:10'),
(23, 'en', 12, 'Breeze Off-Shoulder Dress', '<p>Step into effortless style with the <strong>Breeze Off-Shoulder Dress</strong>. Designed for warm-weather elegance, this dress features a soft, breathable fabric that flows naturally with your movements. The off-shoulder neckline adds a romantic vibe, while the relaxed silhouette ensures all-day comfort. Whether you\'re heading to a beach picnic, brunch date, or evening stroll, this dress is your go-to for easy elegance. Pair it with sandals or heels and simple accessories for a complete look.</p><p><strong>Key Features:</strong></p><ul><li>Off-shoulder neckline</li><li>Lightweight, breathable material</li><li>Relaxed fit with flattering silhouette</li><li>Ideal for casual and semi-formal occasions</li><li>Available in multiple sizes and colors</li></ul>', '<p>Embrace effortless summer style with the <strong>Breeze Off-Shoulder Dress</strong>, designed to keep you cool and stylish all day long.<br>With its lightweight, breathable fabric and flattering off-shoulder neckline, this dress is the perfect blend of comfort and elegance.<br>Whether you\'re heading to a beach getaway, a weekend brunch, or an evening out, this dress will make you feel confident and beautifully relaxed.</p>', '2025-07-08 00:08:54', '2025-07-08 00:08:54'),
(24, 'ar', 12, 'فستان بريز مكشوف الكتفين', '<p>انطلقي في إطلالة أنيقة وبسيطة مع فستان \"بريز\" مكشوف الأكتاف. صُمم هذا الفستان ليناسب أجواء الدفء، فهو مصنوع من قماش ناعم يسمح بمرور الهواء ويتدفق بسلاسة مع حركاتك. يُضفي خط العنق المكشوف الأكتاف لمسة رومانسية، بينما يضمن التصميم المريح راحةً طوال اليوم. سواءً كنتِ متجهة إلى نزهة على الشاطئ، أو موعد غداء، أو نزهة مسائية، فهذا الفستان هو خياركِ الأمثل للأناقة البسيطة. نسقيه مع صندل أو حذاء بكعب عالٍ وإكسسوارات بسيطة لإطلالة متكاملة.</p><p>الميزات الرئيسية:</p><p>خط عنق مكشوف الأكتاف</p><ul><li>مادة خفيفة الوزن تسمح بمرور الهواء</li><li>قصة مريحة مع تصميم جذاب</li><li>مثالي للمناسبات الكاجوال وشبه الرسمية</li><li>متوفر بمقاسات وألوان متعددة</li></ul>', '<p>استمتعي بأناقة صيفية مريحة مع فستان بريز مكشوف الأكتاف، المصمم ليمنحكِ شعورًا بالانتعاش والأناقة طوال اليوم.</p><p>بفضل قماشه الخفيف والمسامي، وخط رقبة مكشوف الأكتاف الجذاب، يُجسد هذا الفستان مزيجًا مثاليًا من الراحة والأناقة.</p><p>سواءً كنتِ متجهة إلى عطلة على الشاطئ، أو إلى غداء نهاية الأسبوع، أو إلى سهرة مسائية، سيمنحكِ هذا الفستان شعورًا بالثقة والاسترخاء.</p>', '2025-07-08 00:08:54', '2025-07-08 00:08:54'),
(25, 'en', 13, 'Midnight Rose Floral Maxi', '<p>The <strong>Midnight Rose Floral Maxi</strong> is where elegance meets nature-inspired charm. This floor-length dress showcases a rich midnight backdrop adorned with soft rose patterns, offering a striking contrast that’s both bold and feminine. Designed to flatter all body types, its flowing silhouette and comfortable fit make it ideal for everything from weddings to evening dinners. The dress features a gentle waistline, soft neckline, and lightweight fabric that moves beautifully with you.</p><p><strong>Highlights:</strong></p><p>All-over romantic floral print</p><p>Deep, elegant midnight color base</p><p>Full-length maxi design</p><p>Soft, breathable, and flowy fabric</p><p>Perfect for parties, formal gatherings, or vacation evenings</p>', '<p>Turn heads with timeless floral elegance in the <strong>Midnight Rose Floral Maxi</strong>.<br>Crafted from soft, flowing fabric, this full-length dress features a romantic rose print against a deep midnight base.<br>Its graceful silhouette and subtle details make it the perfect piece for evening events, garden parties, or special occasions.</p>', '2025-07-08 03:18:11', '2025-07-08 03:18:11'),
(26, 'ar', 13, 'فستان ماكسي زهري منتصف الليل', '<p>فستان ماكسي زهري بنقشة زهور منتصف الليل هو المكان الذي تلتقي فيه الأناقة بسحر الطبيعة. يتميز هذا الفستان الطويل بخلفية غنية بلون منتصف الليل مزينة بنقوش ورود ناعمة، مما يضفي تباينًا رائعًا يجمع بين الجرأة والأنوثة. صُمم ليناسب جميع أنواع الجسم، فتصميمه الانسيابي وقصته المريحة تجعله مثاليًا لجميع المناسبات، من حفلات الزفاف إلى حفلات العشاء المسائية. يتميز الفستان بخصر ناعم وياقة ناعمة وقماش خفيف الوزن يناسب جميع الأذواق.</p><p><br>أبرز المزايا:<br>طبعة زهور رومانسية تغطي الفستان<br>قاعدة بلون منتصف الليل عميق وأنيق<br>تصميم ماكسي بطول كامل<br>قماش ناعم، يسمح بمرور الهواء، وانسيابي<br>مثالي للحفلات، والتجمعات الرسمية، وأمسيات العطلات</p>', '<p>اخطفي الأنظار بأناقتك الزهرية الخالدة مع فستان ماكسي ميدنايت روز فلورال.</p><p>مصنوع من قماش ناعم وخفيف، يتميز هذا الفستان الطويل بطبعة وردة رومانسية على قاعدة داكنة داكنة.</p><p>تصميمه الأنيق وتفاصيله الدقيقة تجعله القطعة المثالية للمناسبات المسائية، أو حفلات الحديقة، أو المناسبات الخاصة.</p>', '2025-07-08 03:18:11', '2025-07-08 03:18:11'),
(27, 'en', 14, 'Button-Down Dress', '<p>The <strong>Button-Down Dress</strong> is a timeless staple with modern flair. Designed for comfort and everyday elegance, it features a classic button-down front, a flattering waistline, and a relaxed yet tailored fit. The dress is made from soft, breathable fabric that’s perfect for year-round wear. Whether layered with a jacket for the office or worn solo for a laid-back outing, it’s a piece that transitions seamlessly through seasons and occasions.</p><p><strong>Features:</strong></p><p>Full front button closure for easy wear</p><p>Tailored fit with relaxed comfort</p><p>Lightweight and breathable fabric</p><p>Ideal for work, casual outings, or layering</p><p>Available in multiple colors and sizes</p>', '<p>Effortlessly stylish and endlessly versatile, the <strong>Button-Down Dress</strong> is your go-to piece for workdays, weekends, and everything in between.<br>Featuring a full front button closure, flattering shape, and breathable fabric, it offers a polished look without sacrificing comfort.<br>Dress it up with heels or keep it casual with sneakers—this wardrobe essential does it all.</p>', '2025-07-08 03:25:30', '2025-07-08 03:25:30'),
(28, 'ar', 14, 'فستان بأزرار', '<p>فستان بأزرار من الأسفل قطعة أساسية خالدة بلمسة عصرية. صُمم ليمنحكِ الراحة والأناقة اليومية، ويتميز بواجهة أمامية كلاسيكية بأزرار من الأسفل، وخصر أنيق، وقصة مريحة ومناسبة. مصنوع من قماش ناعم يسمح بمرور الهواء، مثالي للارتداء على مدار العام. سواء ارتديته مع جاكيت للعمل أو منفردًا لإطلالة مريحة، فهو قطعة أنيقة تتناغم بسلاسة مع مختلف المواسم والمناسبات.</p><p>الميزات:</p><p>إغلاق كامل بأزرار من الأمام لسهولة الارتداء</p><p>قصة مريحة ومريحة</p><p>قماش خفيف الوزن يسمح بمرور الهواء</p><p>مثالي للعمل، أو النزهات الكاجوال، أو للارتداء مع طبقات متعددة</p><p>متوفر بألوان ومقاسات متعددة</p>', '<p>فستان بأزرار، أنيق وبسيط ومتعدد الاستخدامات، هو قطعتكِ المثالية لأيام العمل، وعطلات نهاية الأسبوع، وكل ما بينهما.</p><p>يتميز بإغلاق أمامي كامل بأزرار، وشكل جذاب، وقماش يسمح بمرور الهواء، ليمنحكِ إطلالة أنيقة دون التضحية بالراحة.</p><p>ارتديه مع حذاء بكعب عالٍ أو حذاء رياضي كاجوال - هذا الفستان الأساسي في خزانة ملابسكِ يلبي جميع احتياجاتكِ.</p>', '2025-07-08 03:25:30', '2025-07-08 03:25:30'),
(29, 'en', 15, 'Escape Off-Shoulder Midi', '<p>The <strong>Escape Off-Shoulder Midi</strong> combines comfort with understated elegance. Crafted from lightweight fabric, this midi-length dress is designed with a romantic off-shoulder neckline that frames your collarbone beautifully. The elasticized waist gently defines your shape, while the flowing skirt adds a touch of movement with every step. It’s ideal for warm weather, whether you\'re exploring a new city, attending a casual event, or simply enjoying the day in style.</p><p><strong>Features:</strong></p><p>Off-shoulder neckline for a flattering, feminine look</p><p>Midi length ideal for day-to-evening transitions</p><p>Soft, breathable fabric for all-day comfort</p><p>Relaxed fit with a cinched waist</p><p>Great for travel, daytime events, or casual dinners</p>', '<p>Step into relaxed sophistication with the <strong>Escape Off-Shoulder Midi</strong>—a dress made for sunshine and weekend getaways.<br>With its shoulder-baring neckline, soft cinched waist, and flowing midi length, this piece flatters effortlessly while keeping you cool and comfortable.<br>Perfect for garden parties, vacations, or brunch with friends, it’s your easy-to-style go-to for feminine flair.</p>', '2025-07-08 03:35:49', '2025-07-08 03:35:49'),
(30, 'ar', 15, 'فستان إسكيب متوسط ​​الطول مكشوف الكتفين', '<p>فستان \"إسكيب\" متوسط ​​الطول مكشوف الأكتاف يجمع بين الراحة والأناقة البسيطة. مصنوع من قماش خفيف الوزن، يتميز هذا الفستان متوسط ​​الطول بفتحة رقبة رومانسية مكشوفة الأكتاف تُحيط بعظمة الترقوة بشكل جميل. يُحدد الخصر المرن قوامكِ برقة، بينما تُضفي التنورة المنسدلة لمسة من الحركة مع كل خطوة. إنه مثالي للطقس الدافئ، سواءً كنتِ تستكشفين مدينة جديدة، أو تحضرين مناسبة غير رسمية، أو ببساطة تستمتعين بيومكِ بأناقة.</p><p>الميزات:</p><p>فتحة رقبة مكشوفة الأكتاف لإطلالة أنثوية جذابة</p><p>طول متوسط ​​مثالي للانتقال من النهار إلى المساء</p><p>قماش ناعم ومسامي لراحة طوال اليوم</p><p>قصة مريحة مع خصر مشدود</p><p>مثالي للسفر، والمناسبات النهارية، أو حفلات العشاء غير الرسمية</p>', '<p>انطلقي في أجواء من الرقيّ مع فستان \"إسكيب\" متوسط ​​الطول مكشوف الأكتاف، المصمم خصيصاً لأشعة الشمس وعطلات نهاية الأسبوع.</p><p>بتصميمه المميز بياقة مكشوفة، وخصر ناعم مشدود، وطول متوسط ​​الطول، يُضفي هذا الفستان لمسةً من الأناقة على إطلالتكِ دون عناء، ويحافظ على انتعاشكِ وراحتكِ.</p><p>مثالي لحفلات الحديقة، أو العطلات، أو غداء مع الأصدقاء، فهو خياركِ الأمثل لإطلالة أنثوية ساحرة.</p>', '2025-07-08 03:35:49', '2025-07-08 03:35:49'),
(31, 'en', 16, 'Zee Beach Summer Dress', '<p>The <strong>Cool Beach Summer Dress</strong> is made for warm-weather adventures. Designed with comfort in mind, it features ultra-light fabric that keeps you cool while offering stylish coverage. Its relaxed silhouette allows for easy movement, making it ideal for beach days, vacation lounging, or casual daytime outings. The dress is easy to slip on, pack, and wear—making it a travel essential for effortless summer style.</p><p><strong>Highlights:</strong></p><p>Lightweight and breathable material</p><p>Relaxed, flowy fit for maximum comfort</p><p>Easy to layer over swimwear or wear as-is</p><p>Ideal for beach days, travel, or casual summer outings</p><p>Available in various colors and sizes</p>', '<p>Breeze through sunny days in the <strong>Cool Beach Summer Dress</strong>, your perfect companion for seaside escapes and summer strolls.<br>Lightweight, airy, and effortlessly chic, this dress offers a relaxed fit, breathable fabric, and a style that’s as fresh as a sea breeze.<br>Slip it on over your swimsuit or pair it with sandals for an easygoing, laid-back summer look.</p>', '2025-07-08 03:45:48', '2025-07-08 03:47:07'),
(32, 'ar', 16, 'فستان صيفي رائع للشاطئ', '<p>فستان الشاطئ الصيفي الرائع مصمم لمغامراتك في الأجواء الدافئة. صُمم هذا الفستان مع مراعاة الراحة، فهو مصنوع من قماش خفيف للغاية يحافظ على انتعاشك مع تغطية أنيقة. تصميمه المريح يسمح لك بالحركة بسهولة، مما يجعله مثاليًا لأيام الشاطئ، أو الاسترخاء في العطلات، أو النزهات النهارية غير الرسمية. يتميز الفستان بسهولة الارتداء والحمل، مما يجعله قطعة أساسية في السفر لإطلالة صيفية مريحة.</p><p><br>المزايا:</p><p>مادة خفيفة الوزن وجيدة التهوية</p><p>قصة فضفاضة ومريحة لراحة قصوى</p><p>يسهل ارتداؤه فوق ملابس السباحة أو ارتداؤه كما هو</p><p>مثالي لأيام الشاطئ، أو السفر، أو النزهات الصيفية غير الرسمية</p><p>متوفر بألوان ومقاسات متنوعة</p>', '<p>استمتعي بأيامكِ المشمسة مع فستان \"كول بيتش\" الصيفي، رفيقكِ المثالي لرحلاتكِ على شاطئ البحر والنزهات الصيفية. يتميز هذا الفستان بخفة وزنه وراحته وأناقته، فهو يتميز بقصّة مريحة وقماشه المسامي وأسلوبه المنعش كنسيم البحر. ارتديه فوق ملابس السباحة أو مع صندل لإطلالة صيفية مريحة وبسيطة.</p>', '2025-07-08 03:45:48', '2025-07-08 03:45:48'),
(33, 'en', 17, 'Urban Sands Linen Set', '<p>The <strong>Urban Sands Linen Set</strong> redefines casual sophistication. Designed with a relaxed-yet-refined silhouette, the set features a breezy linen top paired with matching high-waisted trousers or shorts (based on variant). Its neutral tone and breathable texture make it a summer essential, perfect for warm city days, beach towns, or effortless travel looks. Dress it up with heels and accessories or keep it low-key with sandals or sneakers.</p><p><strong>Set Includes:</strong></p><p>Lightweight linen top with relaxed fit</p><p>Matching linen bottoms (trousers or shorts)</p><p>Neutral color palette for versatile styling</p><p>Breathable, natural fabric that keeps you cool</p><p>Easy to mix and match with other wardrobe staples</p>', '<p>Stay cool, confident, and effortlessly chic in the <strong>Urban Sands Linen Set</strong>—a breathable two-piece outfit made for modern city life and laid-back escapes.<br>Crafted from lightweight linen, this matching top and bottom set brings together structure and softness in perfect balance.<br>Whether you\'re out for brunch, on vacation, or working remotely in style, this set blends comfort with contemporary flair.</p>', '2025-07-08 03:52:03', '2025-07-08 03:52:03'),
(34, 'ar', 17, 'مجموعة الكتان أوربان ساندز', '<p>طقم الكتان من \"أوربان ساندز\" يُعيد تعريف مفهوم الأناقة الكاجوال. يتميز الطقم بتصميم مريح وأنيق في آنٍ واحد، ويضم بلوزة كتان خفيفة الوزن مع بنطال أو شورت بخصر عالٍ مطابق (حسب الموديل). لونه المحايد وملمسه المريح يجعله قطعة أساسية في الصيف، مثاليًا لأيام المدينة الدافئة، أو مدن الشاطئ، أو لإطلالات السفر السهلة. ارتديه مع أحذية بكعب عالٍ وإكسسوارات، أو اجعليه بسيطًا مع صندل أو حذاء رياضي.</p><p>يتضمن الطقم:</p><p>بلوزة كتان خفيفة الوزن بقصة مريحة</p><p>سروال كتان مطابق (بنطال أو شورت)</p><p>ألوان محايدة لتنسيقات متعددة</p><p>قماش طبيعي يسمح بمرور الهواء ويحافظ على انتعاشك</p><p>سهل التنسيق مع قطع أساسية أخرى في خزانة الملابس</p>', '<p>حافظي على انتعاشكِ وثقتكِ بأناقتكِ دون عناء مع طقم الكتان \"أوربان ساندز\" - طقم من قطعتين يسمح بمرور الهواء، مصمم لحياة المدينة العصرية والنزهات الهادئة.</p><p>مصنوع من الكتان خفيف الوزن، يجمع هذا الطقم المتناسق بين الجزء العلوي والسفلي بين المتانة والنعومة في توازن مثالي.</p><p>سواء كنتِ في الخارج لتناول فطور متأخر، أو في إجازة، أو تعملين عن بُعد بأناقة، يمزج هذا الطقم بين الراحة واللمسة العصرية.</p>', '2025-07-08 03:52:03', '2025-07-08 03:52:03'),
(35, 'en', 18, 'Sunset Shore Bikini Set', '<p>The <strong>Sunset Shore Bikini Set</strong> is your must-have swimwear for beach getaways and poolside lounging. With its vibrant sunset-inspired hues and supportive design, this two-piece set provides both comfort and confidence in and out of the water. The top features adjustable straps for a customizable fit, while the bottoms offer moderate coverage with a secure waistband. Crafted from high-quality, quick-drying material, it’s made to move with you—perfect for swimming, tanning, or beach volleyball.</p><p><strong>Details:</strong></p><p>Two-piece bikini set with sunset-inspired colors</p><p>Adjustable top for a flexible, secure fit</p><p>Quick-dry, stretchable fabric</p><p>Comfortable moderate coverage bottoms</p><p>Ideal for vacations, pool parties, or beach adventures</p>', '<p>Soak up the sun in style with the <strong>Sunset Shore Bikini Set</strong>, designed to bring a perfect blend of comfort, confidence, and tropical charm.<br>Featuring a flattering cut and secure fit, this bikini set is crafted from quick-dry fabric that feels as good as it looks.<br>Whether you\'re lounging by the pool or diving into waves, it\'s your go-to swimwear for unforgettable beach days.</p>', '2025-07-08 03:56:43', '2025-07-08 03:56:43'),
(36, 'ar', 18, 'طقم بيكيني شاطئ الغروب', '<p>طقم بيكيني \"غروب الشمس\" هو قطعة السباحة الأساسية لعطلات الشاطئ والاسترخاء بجانب المسبح. بألوانه النابضة بالحياة المستوحاة من غروب الشمس وتصميمه الداعم، يوفر هذا الطقم المكون من قطعتين الراحة والثقة داخل وخارج الماء. يتميز الجزء العلوي بأحزمة قابلة للتعديل لملاءمة قابلة للتخصيص، بينما يوفر الجزء السفلي تغطية متوسطة مع حزام خصر محكم. مصنوع من مواد عالية الجودة وسريعة الجفاف، ليتحرك معك أينما ذهبت - مثالي للسباحة، أو التسمير، أو كرة الطائرة الشاطئية.</p><p>التفاصيل:</p><p>طقم بيكيني من قطعتين بألوان مستوحاة من غروب الشمس</p><p>جزء علوي قابل للتعديل لملاءمة مرنة وآمنة</p><p>قماش سريع الجفاف وقابل للتمدد</p><p>جزء سفلي مريح بتغطية متوسطة</p><p>مثالي لقضاء العطلات، أو حفلات المسبح، أو مغامرات الشاطئ</p>', '<p>استمتعي بأشعة الشمس بأناقة مع طقم بيكيني \"صن سيت شور\"، المصمم ليمنحكِ مزيجًا مثاليًا من الراحة والثقة والسحر الاستوائي.</p><p>يتميز هذا الطقم بقصّة أنيقة ومقاس مثالي، وهو مصنوع من قماش سريع الجفاف بملمس رائع ومظهر أنيق.</p><p>سواء كنتِ تسترخي بجانب المسبح أو تغوصين في الأمواج، فهو ملابس السباحة المثالية لأيام شاطئية لا تُنسى.</p>', '2025-07-08 03:56:43', '2025-07-08 03:56:43'),
(37, 'en', 19, 'Crimson Desert Tulle Dress', '<p>The <strong>Crimson Desert Tulle Dress</strong> is a show-stopping piece for those who love to stand out with confidence and grace. Crafted with multiple layers of lightweight tulle, this dress flows beautifully with movement, creating a whimsical, desert-inspired silhouette. The deep crimson color adds richness and intensity, while the fitted bodice balances structure with softness. Ideal for formal events, galas, or statement moments, this dress embodies modern femininity with a dramatic flair.</p><p><strong>Key Features:</strong></p><p>Multi-layered soft tulle for ethereal volume</p><p>Bold crimson color for a striking presence</p><p>Structured bodice for a flattering fit</p><p>Floor or midi-length design (customizable)</p><p>Ideal for weddings, red carpet events, or styled shoots</p>', '<p>Make a bold, graceful statement in the <strong>Crimson Desert Tulle Dress</strong>—a dreamy fusion of elegance and edge.<br>With its layers of soft, airy tulle and a striking crimson hue, this dress brings drama and sophistication to any special occasion.<br>Perfect for evening events, weddings, or photoshoots, it\'s designed to turn heads with every step.</p>', '2025-07-08 04:05:42', '2025-07-08 04:05:42'),
(38, 'ar', 19, 'فستان من التول القرمزي الصحراوي', '<p>فستان التول الصحراوي القرمزي قطعةٌ آسرةٌ لمن يعشقن التألق بثقةٍ وأناقة. مصنوعٌ من طبقاتٍ متعددةٍ من التول الخفيف، يتدفق هذا الفستان برشاقةٍ مع الحركة، ليُضفي عليه لمسةً من سحر الصحراء. يُضفي اللون القرمزي الغامق ثراءً وكثافةً، بينما يُوازن الجزء العلوي المُحكم بين البنية والنعومة. مثاليٌّ للمناسبات الرسمية، وحفلات الجالا، أو اللحظات المميزة، يُجسّد هذا الفستان الأنوثة العصرية بلمسةٍ دراماتيكية.</p><p>الميزات الرئيسية:</p><p>تول ناعم متعدد الطبقات لحجمٍ رقيق</p><p>لون قرمزي جريء لإطلالةٍ آسرة</p><p>جزء علوي مُركّب لملاءمةٍ أنيقة</p><p>تصميمٌ بطولٍ يصل إلى الأرض أو متوسط ​​(قابلٌ للتخصيص)</p><p>مثاليٌّ لحفلات الزفاف، وفعاليات السجادة الحمراء، أو جلسات التصوير المُنسّقة</p>', '<p>تألقي بإطلالة جريئة وأنيقة مع فستان التول الصحراوي القرمزي - مزيج ساحر من الأناقة والجرأة.</p><p>بطبقاته من التول الناعم والخفيف ولون قرمزي أخّاذ، يضفي هذا الفستان لمسةً من الرقي والجاذبية على أي مناسبة خاصة.</p><p>مثالي للمناسبات المسائية، وحفلات الزفاف، وجلسات التصوير، فهو مصمم ليخطف الأنظار في كل خطوة.</p>', '2025-07-08 04:05:42', '2025-07-08 04:05:42'),
(39, 'en', 20, 'Golden Coast Bikini Set', '<p>The <strong>Golden Coast Bikini Set</strong> is designed for the sun-chaser who loves bold style and effortless comfort. Crafted from high-quality stretch fabric with a subtle golden shimmer, this set features a supportive top with adjustable straps and matching bottoms that offer just the right amount of coverage. Whether you\'re relaxing poolside or dancing on the beach at sunset, this bikini moves with you and keeps you glowing from every angle.</p><p><strong>Features:</strong></p><p>Two-piece bikini set with a soft gold sheen</p><p>Adjustable top for personalized fit</p><p>Comfortable, stretchy, quick-drying material</p><p>Mid-rise bottoms with moderate coverage</p><p>Perfect for beach vacations, pool days, and summer getaways</p>', '<p>Shine under the sun in the <strong>Golden Coast Bikini Set</strong>, a glamorous swimwear essential that blends comfort, support, and golden-hour glow.<br>With a flattering silhouette and shimmering finish, this two-piece set is made for days by the sea, pool parties, and tropical escapes.<br>Feel confident, radiant, and ready to turn heads—wherever your summer takes you.</p>', '2025-07-08 04:22:04', '2025-07-08 04:22:04'),
(40, 'ar', 20, 'طقم بيكيني جولدن كوست', '<p>طقم بيكيني جولدن كوست مصمم خصيصاً لعاشقات الشمس اللواتي يعشقن الأناقة الجريئة والراحة الفائقة. مصنوع من قماش مطاطي عالي الجودة بلمسة ذهبية رقيقة، يتميز هذا الطقم بجزء علوي داعم مع حمالات قابلة للتعديل وجزء سفلي متناسق يوفر التغطية المثالية. سواء كنتِ تسترخي بجانب المسبح أو ترقصين على الشاطئ عند غروب الشمس، فإن هذا البكيني يتحرك معكِ ويحافظ على تألقكِ من كل زاوية.</p><p>الميزات:</p><p>طقم بيكيني من قطعتين بلمعة ذهبية ناعمة</p><p>جزء علوي قابل للتعديل لمقاس مخصص</p><p>مادة مريحة، مرنة، وسريعة الجفاف</p><p>جزء سفلي متوسط ​​الارتفاع بتغطية متوسطة</p><p>مثالي لقضاء العطلات على الشاطئ، وأيام المسابح، والرحلات الصيفية</p>', '<p>تألقي تحت أشعة الشمس مع طقم بيكيني جولدن كوست، قطعة أساسية أنيقة لملابس السباحة تجمع بين الراحة والدعم وتألق الساعات الذهبية.</p><p>بتصميم جذاب ولمسة نهائية لامعة، صُممت هذه المجموعة المكونة من قطعتين لأيام على شاطئ البحر، وحفلات المسابح، والرحلات الاستوائية.</p><p>اشعري بالثقة والتألق، واستعدي لخطف الأنظار أينما كان صيفك.</p>', '2025-07-08 04:22:04', '2025-07-08 04:22:04');
